function copyLink(){navigator.clipboard.writeText(window.location.href).then(()=>{alert("Đã sao chép liên kết!")})}function shareToFacebook(){var e=encodeURIComponent(window.location.href);window.open("https://www.facebook.com/sharer/sharer.php?u="+e,"_blank","width=600,height=400")}function shareToTwitter(){var e=encodeURIComponent(window.location.href),t=encodeURIComponent(document.title);window.open(`https://twitter.com/intent/tweet?url=${e}&text=`+t,"_blank","width=600,height=400")}function scrollToTop(){window.scrollTo({top:0,behavior:"smooth"})}function initReadingProgress(){let t=document.createElement("div");t.id="reading-progress",t.style.cssText=`
        position: fixed;
        top: 0;
        left: 0;
        width: 0%;
        height: 4px;
        background: linear-gradient(90deg, #ff5733, #ff7e5f, #feb47b);
        z-index: 9999;
        transition: width 0.1s ease;
    `,document.body.appendChild(t),window.addEventListener("scroll",()=>{var e=window.scrollY/(document.documentElement.scrollHeight-window.innerHeight)*100;t.style.width=e+"%"})}function callNow(){window.location.href="tel:0982722036"}function contactZalo(){window.open("https://zalo.me/0982722036","_blank")}document.addEventListener("DOMContentLoaded",()=>{initReadingProgress();let t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&(e.target.style.opacity="1",e.target.style.transform="translateY(0)")})},{threshold:.1,rootMargin:"0px 0px -50px 0px"});document.querySelectorAll(".blog-card, .chili-type-card, .heat-item, .info-box").forEach(e=>{e.style.opacity="0",e.style.transform="translateY(30px)",e.style.transition="opacity 0.6s ease, transform 0.6s ease",t.observe(e)})});